import React, { useState } from "react";
import { Button, Container, Row, Col } from "react-bootstrap";
import PetSidebar from "./PetSidebar";
import PetInformation from "./PetInformation";
import VaccinationCard from "./VaccinationCard";

export default function UpdatePetProfile() {
  const [key, setKey] = useState("petInformation");

  const handleSelect = (k) => {
    setKey(k);
  };

  const pet = {
    name: "Fluffy",
    type: "Cat",
    breed: "Siamese",
    age: 3,
    weight: "10 lbs",
    image: "https://placekitten.com/200/300",
    description:
      "Fluffy is a playful and affectionate cat who loves to snuggle.",
    vaccinations: [
      {
        name: "Rabies",
        vaccinatedDate: "01/01/2022",
        expiredDate: "01/01/2023",
      },
      {
        name: "Feline Distemper",
        vaccinatedDate: "02/01/2022",
        expiredDate: "02/01/2023",
      },
    ],
  };

  const [petInfo, setPetInfo] = useState({
    name: pet.name,
    type: pet.type,
    breed: pet.breed,
    age: pet.age,
    weight: pet.weight,
    image: pet.image,
    description: pet.description,
  });

  const handlePetInfoChange = (event) => {
    const { id, value } = event.target;
    setPetInfo((prevPetInfo) => ({
      ...prevPetInfo,
      [id]: value,
    }));
  };

  const handleUpdatePet = (event) => {
    event.preventDefault();
    // TODO: handle updating pet in database
    console.log(petInfo);
  };

  const handleDeleteVaccination = (index) => {
    // TODO: handle updating vaccinations in database
    // setPetInfo((prevPetInfo) => ({
    //   ...prevPetInfo,
    //   vaccinations: [
    //     ...prevPetInfo.vaccinations.slice(0, index),
    //     ...prevPetInfo.vaccinations.slice(index + 1),
    //   ],
    // }));
  };

  const handleAddVaccination = () => {
    // setPetInfo((prevPetInfo) => ({
    //   ...prevPetInfo,
    //   vaccinations: [
    //     ...prevPetInfo.vaccinations,
    //     {
    //       name: "",
    //       vaccinatedDate: "",
    //       expiredDate: "",
    //     },
    //   ],
    // }));
  };
  return (
    <Container className="py-5">
      <Row>
        <Col md={4}>
          <PetSidebar onSelect={handleSelect} />
        </Col>
        <Col md={8}>
          {key === "petInformation" && (
            <PetInformation
              petInfo={petInfo}
              onChange={handlePetInfoChange}
              onSubmit={handleUpdatePet}
            />
          )}
          {key === "petVaccination" && (
            <>
              <h4>Vaccinations</h4>
              <hr />
              {pet.vaccinations.map((vaccination, index) => (
                <VaccinationCard
                  key={index}
                  name={vaccination.name}
                  vaccinatedDate={vaccination.vaccinatedDate}
                  expiredDate={vaccination.expiredDate}
                  onDelete={() => handleDeleteVaccination(index)}
                />
              ))}
              <Button className="my-4" onClick={handleAddVaccination}>
                Add Vaccination
              </Button>
            </>
          )}
        </Col>
      </Row>
    </Container>
  );
}
