import { useState } from "react";
import {
  Container,
  Row,
  Col,
  InputGroup,
  FormControl,
  DropdownButton,
  Dropdown,
  Button,
} from "react-bootstrap";

const products = [
  {
    id: 1,
    name: "Blue Buffalo Life Protection Formula Adult Dog Food",
    image:
      "https://images-na.ssl-images-amazon.com/images/I/81yCk1QbTaL._SL1500_.jpg",
    description:
      "Formulated for the health and well-being of dogs, this adult dog food is made with the finest natural ingredients enhanced with vitamins and minerals.",
    price: 48.98,
    rating: 4.6,
    reviews: 19670,
    distance: 2.5,
    store: "PetSmart",
  },
  {
    id: 2,
    name: "Purina Friskies Canned Wet Cat Food",
    image:
      "https://images-na.ssl-images-amazon.com/images/I/81UTc3iLqRL._SL1500_.jpg",
    description:
      "This pack of wet cat food includes 40 cans of different flavors to satisfy your cat's cravings. Made with real meat and fish for high-quality protein.",
    price: 20.48,
    rating: 4.5,
    reviews: 2744,
    distance: 1.8,
    store: "Petco",
  },
];

export default function SearchResult() {
  const [searchInput, setSearchInput] = useState("");
  const [optionsFilter, setOptionsFilter] = useState("All");

  const handleSearchInputChange = (event) => {
    setSearchInput(event.target.value);
  };

  const handleOptionsFilterChange = (event) => {
    setOptionsFilter(event.target.innerText);
  };

  const filteredResults = products.filter((product) => {
    const searchMatch =
      product.name.toLowerCase().indexOf(searchInput.toLowerCase()) !== -1;
    const optionsMatch =
      optionsFilter === "All" || optionsFilter === product.type;
    return searchMatch && optionsMatch;
  });

  return (
    <Container>
      <Row className="my-4 py-5">
        <Col>
          <InputGroup>
            <DropdownButton
              as={InputGroup.Prepend}
              variant="outline-secondary"
              title={optionsFilter}
              id="input-group-dropdown-1"
            >
              <Dropdown.Item onClick={handleOptionsFilterChange}>
                All
              </Dropdown.Item>
              <Dropdown.Item onClick={handleOptionsFilterChange}>
                Food
              </Dropdown.Item>
              <Dropdown.Item onClick={handleOptionsFilterChange}>
                Shelter
              </Dropdown.Item>
            </DropdownButton>
            <FormControl
              type="text"
              placeholder="Enter search term"
              value={searchInput}
              onChange={handleSearchInputChange}
            />
            <InputGroup.Append>
              <Button variant="primary" type="submit">
                Search
              </Button>
            </InputGroup.Append>
          </InputGroup>
        </Col>
      </Row>
      <Row>
        {filteredResults.map((result) => (
          <Col xs={12} sm={6} md={4} lg={3} key={result.id}>
            <div className="border mb-4 p-3">
              <div className="text-center">
                <img
                  src={result.image}
                  alt={result.name}
                  className="img-fluid"
                />
              </div>
              <h5 className="mt-3">{result.name}</h5>
              <p>{result.description}</p>
              <p>
                <strong>Price:</strong> ${result.price}
              </p>
              <p>
                <strong>Rating:</strong> {result.rating} ({result.reviews}{" "}
                reviews)
              </p>
              <p>
                <strong>Store:</strong> {result.store} ({result.distance} mi
                away)
              </p>
            </div>
          </Col>
        ))}
      </Row>
    </Container>
  );
}
