import React from "react";
import { Card, Button } from "react-bootstrap";
import { useHistory } from "react-router-dom";

const PetProfile = ({ pets, userId }) => {
  const history = useHistory();

  const handleAddPet = () => {
    history.push({
      pathname: "/add-pet",
      state: { userId: userId },
    });
  };

  return (
    <div>
      <h4>Your Pets</h4>
      <hr />
      {pets.map((pet, index) => (
        <Card key={index} style={{ width: "18rem", marginBottom: "1rem" }}>
          <Card.Img variant="top" src={pet.petImage} />
          <Card.Body>
            <Card.Title>{pet.petName}</Card.Title>
            <Card.Text>Breed: {pet.petType}</Card.Text>
            <Card.Text>Age: {pet.petAge}</Card.Text>
            <Card.Text>Weight: {pet.petWeight} lbs</Card.Text>
            <Button>Update</Button>
            <Button className="btn btn-danger mx-2">Delete</Button>
          </Card.Body>
        </Card>
      ))}
      <Button onClick={handleAddPet}>Add Pet</Button>
    </div>
  );
};

export default PetProfile;
