import React from "react";
import { Card, Col, Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import axios from "axios";

const PetCard = ({ pet, handleDeletePet, handleFindFood }) => {
  const { petId, petName, petType, petAge, petWeight, petImage } = pet;

  const handleDelete = async () => {
    try {
      await axios.delete(`http://localhost:8080/deletePet/${petId}`);
      alert(`Successfully delete ${petName} from your pet list`);
      handleDeletePet(petId);
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <Col sm={6} md={4} lg={3}>
      <Card className="mx-3" style={{ height: "100%", width: "100%" }}>
        <Card.Img variant="top" src={petImage} />
        <Card.Body>
          <Card.Title>{petName}</Card.Title>
          <Card.Text>Breed: {petType}</Card.Text>
          <Card.Text>Age: {petAge}</Card.Text>
          <Card.Text>Weight: {petWeight} lbs</Card.Text>
          <Link
            className="btn btn-primary"
            to="/updatePetProfile"
            state={{ petDetail: pet }}
          >
            More
          </Link>
          <Button
            className="btn btn-secondary mx-1"
            onClick={() => handleFindFood(pet)}
          >
            Food
          </Button>
          <Button className="btn btn-danger m-1" onClick={handleDelete}>
            X
          </Button>
        </Card.Body>
      </Card>
    </Col>
  );
};

export default PetCard;
