import React, { useState } from "react";
import { Button, Container, Row, Col } from "react-bootstrap";
import { useLocation } from "react-router-dom";
import PetSidebar from "./PetSidebar";
import PetInformation from "./PetInformation";
import VaccinationCard from "./VaccinationCard";

export default function UpdatePetProfile() {
  const [key, setKey] = useState("petInformation");
  const handleSelect = (k) => {
    setKey(k);
  };
  const location = useLocation();
  const { petDetail } = location.state;
  console.log(petDetail);

  const [petInfo, setPetInfo] = useState({
    name: petDetail.petName,
    type: petDetail.petType,
    breed: petDetail.petBreed,
    age: petDetail.petAge,
    weight: petDetail.petWeight,
    image: petDetail.petImage,
    description: petDetail.petDescription,
    vaccinations: petDetail.petVaccinations,
  });

  // const handlePetInfoChange = (event) => {
  //   const { id, value } = event.target;
  //   setPetInfo((prevPetInfo) => ({
  //     ...prevPetInfo,
  //     [id]: value,
  //   }));
  // };

  const handleDeleteVaccination = (index) => {
    // TODO: handle updating vaccinations in database
    // setPetInfo((prevPetInfo) => ({
    //   ...prevPetInfo,
    //   vaccinations: [
    //     ...prevPetInfo.vaccinations.slice(0, index),
    //     ...prevPetInfo.vaccinations.slice(index + 1),
    //   ],
    // }));
  };

  const handleAddVaccination = () => {
    // setPetInfo((prevPetInfo) => ({
    //   ...prevPetInfo,
    //   vaccinations: [
    //     ...prevPetInfo.vaccinations,
    //     {
    //       name: "",
    //       vaccinatedDate: "",
    //       expiredDate: "",
    //     },
    //   ],
    // }));
  };
  return (
    <Container className="py-5">
      <Row>
        <Col md={4}>
          <PetSidebar onSelect={handleSelect} />
        </Col>
        <Col md={8}>
          {key === "petInformation" && <PetInformation petInfo={petInfo} />}
          {key === "petVaccination" && (
            <>
              <h4>Vaccinations</h4>
              <hr />
              {petInfo.vaccinations.map((vaccination, index) => (
                <VaccinationCard
                  key={index}
                  name={vaccination.name}
                  vaccinatedDate={vaccination.vaccinatedDate}
                  expiredDate={vaccination.expiredDate}
                  onDelete={() => handleDeleteVaccination(index)}
                />
              ))}
              <Button className="my-4" onClick={handleAddVaccination}>
                Add Vaccination
              </Button>
            </>
          )}
        </Col>
      </Row>
    </Container>
  );
}
