import React, { useState } from "react";
import { Container } from "react-bootstrap";
import { useLocation } from "react-router-dom";

function AddVaccinationForm() {
  const [vacName, setVacName] = useState("");
  const [vacNumber, setVacNumber] = useState("");
  const [expiredDate, setExpiredDate] = useState("");
  const [vacRecord, setVacRecord] = useState("");

  const location = useLocation();
  const { petDetail } = location.state;
  console.log(petDetail);

  function handleSubmit(event) {
    event.preventDefault();
    const newVaccination = {
      vacName: vacName,
      vacNumber: vacNumber,
      expiredDate: expiredDate,
      vacRecord: vacRecord,
      petId: petDetail.petId,
    };

    setVacName("");
    setVacNumber("");
    setExpiredDate("");
    setVacRecord("");
  }

  return (
    <Container>
      <form onSubmit={handleSubmit}>
        <label>
          Vaccination Name:
          <input
            type="text"
            value={vacName}
            onChange={(event) => setVacName(event.target.value)}
          />
        </label>
        <label>
          Vaccination Number:
          <input
            type="text"
            value={vacNumber}
            onChange={(event) => setVacNumber(event.target.value)}
          />
        </label>
        <label>
          Expired Date:
          <input
            type="date"
            value={expiredDate}
            onChange={(event) => setExpiredDate(event.target.value)}
          />
        </label>
        <label>
          Vaccination Record:
          <input
            type="text"
            value={vacRecord}
            onChange={(event) => setVacRecord(event.target.value)}
          />
        </label>
      </form>
    </Container>
  );
}

export default AddVaccinationForm;
