import React, { useState } from "react";
import {
  InputGroup,
  Form,
  DropdownButton,
  Dropdown,
  Button,
} from "react-bootstrap";

export default function SearchBar() {
  const [selectedOption, setSelectedOption] = useState("Options");
  const [address, setAddress] = useState("");
  const [city, setCity] = useState("");
  const [zipcode, setZipcode] = useState("");

  const handleSearch = () => {
    console.log("Address:", address);
    console.log("City:", city);
    console.log("Zip Code:", zipcode);
  };

  return (
    <InputGroup>
      <Form.Control
        style={{ height: "50px", width: "40%", fontWeight: "bold" }}
        type="search"
        id="address"
        placeholder="User's address"
        onChange={(e) => setAddress(e.target.value)}
      />
      <Form.Control
        style={{ height: "50px", width: "20%", fontWeight: "bold" }}
        type="search"
        id="city"
        placeholder="City"
        onChange={(e) => setCity(e.target.value)}
      />
      <Form.Control
        style={{ height: "50px", width: "20%", fontWeight: "bold" }}
        type="search"
        id="zipcode"
        placeholder="Zip Code"
        onChange={(e) => setZipcode(e.target.value)}
      />
      <DropdownButton
        variant="outline-secondary"
        title={selectedOption || "Options"}
        id="input-group-dropdown-1"
        onSelect={(eventKey) => setSelectedOption(eventKey)}
      >
        <Dropdown.Item href="#" id="store" eventKey="store">
          Store
        </Dropdown.Item>
        <Dropdown.Item href="#" id="shelter" eventKey="shelter">
          Shelter
        </Dropdown.Item>
      </DropdownButton>
      <Button variant="primary" onClick={handleSearch}>
        <i className="fas fa-search"></i>
      </Button>
    </InputGroup>
  );
}
