// export default petReducer;

const initialState = {
  petList: [],
};

const petReducer = (state = initialState, action) => {
  switch (action.type) {
    case "ADD_PET":
      const newPet = { ...action.payload, listVaccination: [] };
      return {
        ...state,
        petList: [...state.petList, newPet],
      };
    case "ADD_PET_LIST":
      return {
        ...state,
        petList: state.petList.map((pet) => {
          if (pet.petId === action.payload.petId) {
            return {
              ...pet,
              listVaccination: action.payload.listVaccination,
            };
          } else {
            return pet;
          }
        }),
      };
    case "ADD_VACCINATION":
      return {
        ...state,
        petList: state.petList.map((pet) => {
          if (pet.petId === action.payload.petId) {
            const updatedVaccinationList = [
              ...pet.vaccinationList,
              action.payload.vaccination,
            ];
            return { ...pet, vaccinationList: updatedVaccinationList };
          } else {
            return pet;
          }
        }),
      };
    default:
      return state;
  }
};

export default petReducer;
