const initialState = {
  petList: [],
};

const petReducer = (state = initialState, action) => {
  switch (action.type) {
    case "ADD_PET":
      return {
        ...state,
        petList: [...state.petList, action.payload],
      };
    case "ADD_PET_LIST":
      return {
        ...state,
        petList: action.payload,
      };
    default:
      return state;
  }
};

export default petReducer;
