import React, { useState } from "react";
import { Container, Form, Button } from "react-bootstrap";
import axios from "axios";

const AddPet = ({ userId, onAddPet }) => {
  const [petName, setPetName] = useState("");
  const [petType, setPetType] = useState("");
  const [petAge, setPetAge] = useState("");
  const [petImage, setPetImage] = useState("");
  const [petWeight, setPetWeight] = useState("");

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post(`/savePet/${userId}`, {
        petName,
        petType,
        petAge,
        petImage,
        petWeight,
      });
      onAddPet(response.data);
      setPetName("");
      setPetType("");
      setPetAge("");
      setPetImage("");
      setPetWeight("");
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <Container>
      <div>
        <h4>Add Pet</h4>
        <hr />
        <Form onSubmit={handleSubmit}>
          <Form.Group controlId="petName">
            <Form.Label>Pet Name</Form.Label>
            <Form.Control
              type="text"
              value={petName}
              onChange={(e) => setPetName(e.target.value)}
              required
            />
          </Form.Group>
          <Form.Group controlId="petType">
            <Form.Label>Pet Type</Form.Label>
            <Form.Control
              type="text"
              value={petType}
              onChange={(e) => setPetType(e.target.value)}
              required
            />
          </Form.Group>
          <Form.Group controlId="petAge">
            <Form.Label>Pet Age</Form.Label>
            <Form.Control
              type="number"
              value={petAge}
              onChange={(e) => setPetAge(e.target.value)}
              required
            />
          </Form.Group>
          <Form.Group controlId="petImage">
            <Form.Label>Pet Image URL</Form.Label>
            <Form.Control
              type="text"
              value={petImage}
              onChange={(e) => setPetImage(e.target.value)}
              required
            />
          </Form.Group>
          <Form.Group controlId="petWeight">
            <Form.Label>Pet Weight</Form.Label>
            <Form.Control
              type="number"
              value={petWeight}
              onChange={(e) => setPetWeight(e.target.value)}
              required
            />
          </Form.Group>
          <Button variant="primary" type="submit">
            Save Pet
          </Button>
        </Form>
      </div>
    </Container>
  );
};

export default AddPet;
