const initialState = {
  user: null,
};

export const userReducer = (state = initialState, action) => {
  console.log("user login", action);
  switch (action.type) {
    case "LOGIN":
      return {
        ...state,
        user: action.payload,
      };
    case "LOGOUT":
      return {
        ...state,
        user: null,
      };
    default:
      return state;
  }
};
