  //   description: pet.description,
  // });
  const [petInfo, setPetInfo] = useState({
    name: pet2.name,
    type: pet2.type,
    breed: pet2.breed,
    age: pet2.age,
    weight: pet2.weight,
    image: pet2.image,
    description: pet2.description,
    vaccinations: pet2.vaccinations,
  });

  const handlePetInfoChange = (event) => {
    const { id, value } = event.target;
    setPetInfo((prevPetInfo) => ({
      ...prevPetInfo,
      [id]: value,
    }));
  };

  const handleUpdatePet = (event) => {
