    //     {
    //       name: "",
    //       vaccinatedDate: "",
    //       expiredDate: "",
    //     },
    //   ],
    // }));
  };
  return (
    <Container className="py-5">
      <Row>
        <Col md={4}>
          <PetSidebar onSelect={handleSelect} />
        </Col>
        <Col md={8}>
          {key === "petInformation" && (
            <PetInformation
              petInfo={petInfo}
              onChange={handlePetInfoChange}
              onSubmit={handleUpdatePet}
            />
          )}
          {key === "petVaccination" && (
            <>
              <h4>Vaccinations</h4>
              <hr />
              {pet.vaccinations.map((vaccination, index) => (
                <VaccinationCard
                  key={index}
                  name={vaccination.name}
                  vaccinatedDate={vaccination.vaccinatedDate}
                  expiredDate={vaccination.expiredDate}
                  onDelete={() => handleDeleteVaccination(index)}
                />
              ))}
              <Button className="my-4" onClick={handleAddVaccination}>
                Add Vaccination
              </Button>
            </>
          )}
        </Col>
      </Row>
    </Container>
  );
}
