import React, { useState } from "react";
import {
  Container,
  Row,
  Col,
  Form,
  Button,
  InputGroup,
  DropdownButton,
  Dropdown,
} from "react-bootstrap";
import StoreCard from "./StoreCard";

const results = [
  {
    id: 1,
    name: "Product 1",
    description: "Description of Product 1",
    rating: 4,
    store: "Store 1",
    distance: 1.2,
    location: "Location 1",
    image: "https://via.placeholder.com/150",
    price: 10.99,
  },
  {
    id: 2,
    name: "Product 2",
    description: "Description of Product 2",
    rating: 3,
    store: "Store 2",
    distance: 2.5,
    location: "Location 2",
    image: "https://via.placeholder.com/150",
    price: 20.99,
  },
  {
    id: 3,
    name: "Product 3",
    description: "Description of Product 3",
    rating: 5,
    store: "Store 3",
    distance: 3.7,
    location: "Location 3",
    image: "https://via.placeholder.com/150",
    price: 15.99,
  },
];

export default function SearchResult() {
  const [distanceFilter, setDistanceFilter] = useState(0);
  const [priceSort, setPriceSort] = useState(null);
  // const ProductCard = ({ product }) => (
  //   <Card className="h-60">
  //     <Row>
  //       <Col xs={5}>
  //         <Card.Img
  //           variant="top"
  //           src={product.image}
  //           style={{ width: "100%" }}
  //         />
  //       </Col>
  //       <Col xs={7}>
  //         <Card.Body>
  //           <Card.Title>{product.name}</Card.Title>
  //           <Card.Text>{product.description}</Card.Text>
  //           <Card.Text>{product.rating} stars</Card.Text>
  //           <Card.Text>{product.store}</Card.Text>
  //           <Card.Text>{product.distance} miles from your location</Card.Text>
  //           <Card.Text>{product.location}</Card.Text>
  //           <Card.Text>${product.price}</Card.Text>
  //         </Card.Body>
  //       </Col>
  //     </Row>
  //   </Card>
  // );

  const handleDistanceFilterChange = (event) => {
    setDistanceFilter(Number(event.target.value));
  };

  const handlePriceSortChange = (event) => {
    setPriceSort(event.target.value);
  };

  let filteredResults = results.filter(
    (result) => result.distance <= distanceFilter
  );

  if (priceSort === "lowToHigh") {
    filteredResults = filteredResults.sort((a, b) => a.price - b.price);
  } else if (priceSort === "highToLow") {
    filteredResults = filteredResults.sort((a, b) => b.price - a.price);
  }

  return (
    <Container>
      <Row className="my-4 py-5">
        <Col className="d-flex justify-content-center">
          <InputGroup>
            <Form.Control
              style={{ height: "50px", fontWeight: "bold" }}
              type="search"
              placeholder="Search"
            />
            <DropdownButton
              variant="outline-secondary"
              title="Options"
              id="input-group-dropdown-1"
            >
              <Dropdown.Item href="#">Distance</Dropdown.Item>
              <Dropdown.Item href="#">Price</Dropdown.Item>
            </DropdownButton>
            <Button variant="primary">
              <i className="fas fa-search"></i>
            </Button>
          </InputGroup>
        </Col>
      </Row>
      <Row>
        <Col>
          <h5>Search Results</h5>
        </Col>
      </Row>
      <Row className="mb-4">
        <Col>
          <Form>
            <Form.Group controlId="formDistance">
              <Form.Label>Distance (miles)</Form.Label>
              <Form.Control
                type="number"
                placeholder="Enter maximum distance"
                value={distanceFilter}
                onChange={handleDistanceFilterChange}
              />
            </Form.Group>
            <Form.Group controlId="formPriceSort">
              <Form.Label>Sort by Price</Form.Label>
              <Form.Control as="select" onChange={handlePriceSortChange}>
                <option value="">Select an option</option>
                <option value="lowToHigh">Low to High</option>
                <option value="highToLow">High to Low</option>
              </Form.Control>
            </Form.Group>
          </Form>
        </Col>
      </Row>
      <Row>
        {filteredResults.map((result) => (
          <Col lg={12}>
            <StoreCard store={result} />
          </Col>
        ))}
      </Row>
      <hr />
    </Container>
  );
}
