import React, { useState } from "react";
import { Button, Row, Col } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import PetCard from "../PetProfile/PetCard";
const PetProfile = ({ pets, userId }) => {
  const navigate = useNavigate();
  const [petList, setPetList] = useState(pets);

  const handleAddPet = () => {
    navigate({
      pathname: "/addPet",
    });
  };

  const handleDeletePet = (petId) => {
    const updatedPetList = petList.filter((pet) => pet.petId !== petId);
    setPetList(updatedPetList);
  };

  const handleFindFood = (pet) => {
    // handle finding food for pet
  };

  const petCards = petList.map((pet, index) => (
    <Col md={6} key={index} style={{ marginBottom: "1rem" }}>
      <PetCard
        pet={pet}
        handleDeletePet={handleDeletePet}
        handleFindFood={handleFindFood}
      />
    </Col>
  ));

  const petCardRows = [];
  for (let i = 0; i < petCards.length; i += 2) {
    const cardRow = (
      <Row key={i / 2} style={{ marginBottom: "1rem" }}>
        {petCards.slice(i, i + 2)}
      </Row>
    );
    petCardRows.push(cardRow);
  }

  return (
    <div>
      <h4>Your Pets</h4>
      <hr />
      <Row className="pb-4">
        {petList.map((pet, index) => (
          <PetCard key={pet.petId} pet={pet} handleFindFood={handleFindFood} />
        ))}
      </Row>
    </div>
  );
};

export default PetProfile;
