import React, { useState } from "react";
import { Container, Row, Col } from "react-bootstrap";
import UserProfile from "./UserProfile";
import PetProfile from "./PetProfile";
import LoginSecurity from "./LoginSecurity";
import Sidebar2 from "./Sidebar2";
export default function User() {
  const [selected, setSelected] = useState("profile");

  const handleSelect = (selectedKey) => {
    setSelected(selectedKey);
  };

  const renderSelectedComponent = () => {
    switch (selected) {
      case "profile":
        return (
          <UserProfile name="John Doe" address="123 Main St" dob="01-01-1990" />
        );
      case "petProfile":
        return (
          <PetProfile
            pets={[
              {
                name: "Fluffy",
                image: "https://via.placeholder.com/150",
                description: "Cute and cuddly",
              },
            ]}
          />
        );
      case "loginSecurity":
        return (
          <LoginSecurity
            email="john.doe@example.com"
            phone="+1 (234) 567-8901"
          />
        );
      default:
        return <div>Please select an option from the sidebar.</div>;
    }
  };

  return (
    <Container className="py-5">
      <Row>
        <Col md={4}>
          <Sidebar2 onSelect={handleSelect} />
        </Col>
        <Col md={8}>{renderSelectedComponent()}</Col>
      </Row>
    </Container>
  );
}
