import React from "react";
import { Card, Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import axios from "axios";

const PetCard = ({ pet, handleDeletePet, handleFindFood }) => {
  const { petId, petName, petType, petAge, petWeight, petImage } = pet;

  const handleDelete = async () => {
    try {
      await axios.delete(`http://localhost:8080/deletePet/${petId}`);
      alert(`Successfully delete ${petName} from your pet list`);
      handleDeletePet(petId);
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <Card style={{ height: "100%" }}>
      <Card.Img variant="top" src={petImage} />
      <Card.Body>
        <Card.Title>{petName}</Card.Title>
        <Card.Text>Breed: {petType}</Card.Text>
        <Card.Text>Age: {petAge}</Card.Text>
        <Card.Text>Weight: {petWeight} lbs</Card.Text>
        <Link
          className="btn btn-primary"
          to="/updatePetProfile"
          state={{ petDetail: pet }}
        >
          Details
        </Link>
        <Button
          className="btn btn-secondary mx-2"
          onClick={() => handleFindFood(pet)}
        >
          Find Food
        </Button>
        <Button className="btn btn-danger m-2" onClick={handleDelete}>
          Delete
        </Button>
      </Card.Body>
    </Card>
  );
};

export default PetCard;
