            />
          </Carousel.Item>
          <Carousel.Item>
            <img
              className="d-block w-100"
              src="https://via.placeholder.com/1200x400.png?text=Banner+3"
              alt="Third slide"
            />
          </Carousel.Item>
        </Carousel>
        {/* Your pets section */}
      </Container>
      <Container>
        <Row className="my-4 py-5">
          <Col className="d-flex justify-content-center">
            <SearchBar />
          </Col>
        </Row>
        <hr />
        <Row>
          <Col>
            <h5>Your Pets</h5>
          </Col>
        </Row>
        <Row className="pb-4">
          {petList.map((pet, index) => (
            <PetCard
