import { useState } from "react";
import { Container, Row, Col, Form, Button } from "react-bootstrap";

export default function SearchResult() {
  // Set up state variables for search input and distance filter
  const [searchInput, setSearchInput] = useState("");
  const [distanceFilter, setDistanceFilter] = useState(10); // Default filter value is 10 miles

  // Dummy data for search results
  const searchResults = [
    {
      id: 1,
      name: "Pet Haven",
      description: "Premium pet supplies for dogs and cats",
      price: 19.99,
      image: "https://via.placeholder.com/350x250",
      rating: 4.5,
      reviews: 10,
      distance: 5,
    },
    {
      id: 2,
      name: "Furry Friends",
      description: "Adorable stuffed animals for kids",
      price: 12.99,
      image: "https://via.placeholder.com/350x250",
      rating: 4.0,
      reviews: 5,
      distance: 15,
    },
  ];

  // Handle search input change
  const handleSearchInputChange = (e) => {
    setSearchInput(e.target.value);
  };

  // Handle distance filter change
  const handleDistanceFilterChange = (e) => {
    setDistanceFilter(e.target.value);
  };

  // Filter search results by distance
  const filteredResults = searchResults.filter(
    (result) => result.distance <= distanceFilter
  );

  return (
    <Container>
      <Row>
        <Col>
          <h1>Search Results</h1>
        </Col>
      </Row>
      <Row>
        <Col>
          <Form>
            <Form.Group controlId="searchInput">
              <Form.Label>Search</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter search term"
                value={searchInput}
                onChange={handleSearchInputChange}
              />
            </Form.Group>
            <Form.Group controlId="distanceFilter">
              <Form.Label>Distance (miles)</Form.Label>
              <Form.Control
                as="select"
                value={distanceFilter}
                onChange={handleDistanceFilterChange}
              >
                <option value={5}>5</option>
                <option value={10}>10</option>
                <option value={15}>15</option>
              </Form.Control>
            </Form.Group>
            <Button variant="primary" type="submit">
              Search
            </Button>
          </Form>
        </Col>
      </Row>
      <Row>
        {filteredResults.map((result) => (
          <Col key={result.id} sm={6} md={4} lg={3}>
            <div className="border rounded p-3 mb-3">
              <div className="position-relative">
                <img
                  className="w-100 mb-3"
                  src={result.image}
                  alt={result.name}
                />
                <div className="position-absolute top-0 start-0 p-2">
                  <span className="badge bg-danger text-white">New</span>
                </div>
              </div>
              <div>
                <h5>{result.name}</h5>
                <p>{result.description}</p>
                <p>{`$${result.price}`}</p>
                <p>{`${result.rating} stars (${result.reviews})`}</p>
                <p>{`Distance: ${result.distance} miles`}</p>
                <p>{`Store: ${result.name}`}</p>
              </div>
            </div>
          </Col>
        ))}
      </Row>
    </Container>
  );
}
