  //       );
  //     case "loginSecurity":
  //       return (
  //         <LoginSecurity
  //           email="john.doe@example.com"
  //           phone="+1 (234) 567-8901"
  //         />
  //       );
  //     default:
  //       return <div>Please select an option from the sidebar.</div>;
  //   }
  // };

  const renderSelectedComponent = () => {
    if (isLoggedIn && user) {
      switch (selected) {
        case "profile":
          return (
            <UserProfile
              name={user.name}
              address={user.address}
              dob={user.dob}
            />
          );
        case "petProfile":
          return <PetProfile pets={user.pets} />;
        case "loginSecurity":
          return <LoginSecurity email={user.email} phone={user.phone} />;
        default:
          return <div>Please select an option from the sidebar.</div>;
      }
    } else {
      alert("Please login to view this page.");
      // Redirect to login page using your preferred method
    }
  };
  return (
    <Container className="py-5">
      <Row>
        <Col md={4}>
          <Sidebar2 onSelect={handleSelect} />
        </Col>
        <Col md={8}>{renderSelectedComponent()}</Col>
      </Row>
    </Container>
  );
}
