import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { Nav, Navbar, NavDropdown } from "react-bootstrap";
import Container from "react-bootstrap/Container";
import logo from "../../images/favicon.ico";
import { useReducer } from "react";

export default function Header() {
  const { user, isLoggedIn } = useSelector((state) => state(userReducer.user));
  console.log("header", user);
  const dispatch = useDispatch();
  const handleLogout = () => {
    dispatch({
      type: "LOGOUT_SUCCESS",
      isLoggedIn: false,
    });
  };
  return (
    <Navbar bg="dark" variant="dark" sticky="top">
      <Container>
        <Navbar.Brand href="/home">
          <img
            alt=""
            src={logo}
            width="30"
            height="30"
            className="d-inline-block align-top"
          />{" "}
          PetSafe+
        </Navbar.Brand>
        <Nav>
          {user && isLoggedIn ? (
            <NavDropdown
              title={`Welcome, ${user.userName}`}
              id="basic-nav-dropdown"
            >
              <NavDropdown.Item href="/user">User Profile</NavDropdown.Item>
              <NavDropdown.Item href="/logout">Pet Profile</NavDropdown.Item>
              <NavDropdown.Item href="/logout" onClick={handleLogout}>
                Logout
              </NavDropdown.Item>
            </NavDropdown>
          ) : (
            <NavDropdown
              title={
                <span>
                  <i className="fal fa-user"></i>Login/Create Account
                </span>
              }
              id="basic-nav-dropdown"
            >
              <NavDropdown.Item href="/login">Login</NavDropdown.Item>
              <NavDropdown.Item href="/signup">Create Account</NavDropdown.Item>
            </NavDropdown>
          )}
        </Nav>
      </Container>
    </Navbar>
  );
}
