import React from "react";
import { Card, Col, Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import axios from "axios";

const PetCard = ({ pet }) => {
  const { petId, petName, petType, petAge, petWeight, petImage } = pet;

  //   const handleDelete = async () => {
  //     try {
  //       await axios.delete(`http://localhost:8080/deletePet/${petId}`);
  //       alert(`Successfully delete ${petName} from your pet list`);
  //       handleDeletePet(petId);
  //     } catch (error) {
  //       console.log(error);
  //     }
  //   };
  const handleFindFood = async (petType, petAge, petWeight) => {
    console.log("handleFindFood Called.");
    try {
      const response = await axios.get(
        `http://localhost:8080/findFoodForPet/${petType}/${petAge}/${petWeight}`
      );
      const searchResult = response.data;
      console.log(searchResult);
      // Do something with the search result, such as showing it in a modal
    } catch (error) {
      console.log(error);
    }
  };
  return (
    <Col sm={6} md={4} lg={3}>
      <Card className="mx-3" style={{ height: "100%", width: "100%" }}>
        <Card.Img variant="top" src={petImage} />
        <Card.Body>
          <Card.Title>{petName}</Card.Title>
          <Card.Text>Breed: {petType}</Card.Text>
          <Card.Text>Age: {petAge}</Card.Text>
          <Card.Text>Weight: {petWeight} lbs</Card.Text>
          <Link
            className="btn btn-primary"
            to="/updatePetProfile"
            state={{ petDetail: pet }}
          >
            More
          </Link>
          <Button
            className="btn btn-secondary mx-1"
            onClick={() => handleFindFood()}
          >
            Find Food
          </Button>
        </Card.Body>
      </Card>
    </Col>
  );
};

export default PetCard;
