                placeholder="User's address"
              />
              <Form.Control
                style={{ height: "50px", width: "20%", fontWeight: "bold" }}
                type="search"
                id="city"
                placeholder="City"
              />
              <Form.Control
                style={{ height: "50px", width: "20%", fontWeight: "bold" }}
                type="search"
                id="zipcode"
                placeholder="Zip Code"
              />
              <DropdownButton
                variant="outline-secondary"
                title={selectedOption || "Options"}
                id="input-group-dropdown-1"
                onSelect={(eventKey) => setSelectedOption(eventKey)}
              >
                <Dropdown.Item href="#" id="store" eventKey="store">
                  Store
                </Dropdown.Item>
                <Dropdown.Item href="#" id="shelter" eventKey="shelter">
                  Shelter
                </Dropdown.Item>
              </DropdownButton>
              <Button variant="primary">
                <i className="fas fa-search"></i>
              </Button>
            </InputGroup> */}

            <SearchBar />
          </Col>
        </Row>
        <hr />
        <Row>
          <Col>
            <h5>Your Pets</h5>
          </Col>
        </Row>
        <Row className="pb-4">
          {petList.map((pet, index) => (
            <PetCard
              key={pet.petId}
              pet={pet}
              handleFindFood={handleFindFood}
            />
          ))}
        </Row>
        <hr />
      </Container>
      <link
        href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.15.3/css/all.min.css"
        rel="stylesheet"
      />
    </>
  );
}
