import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import { Container, Row, Col } from "react-bootstrap";
import UserProfile from "./UserProfile";
import PetProfile from "./PetProfile";
import LoginSecurity from "./LoginSecurity";
import Sidebar2 from "./Sidebar2";
import { useNavigate } from "react-router-dom";
export default function User() {
  const { user, isLoggedIn } = useSelector((state) => state["user"]);
  const [selected, setSelected] = useState("profile");
  console.log(user);
  const navigate = useNavigate();
  const handleSelect = (selectedKey) => {
    setSelected(selectedKey);
  };

  const renderSelectedComponent = () => {
    if (user && isLoggedIn) {
      switch (selected) {
        case "profile":
          return (
            <UserProfile
              name={user.name}
              address={user.address}
              dob={user.dob}
            />
          );
        case "petProfile":
          return <PetProfile pets={user.pets} />;
        case "loginSecurity":
          return (
            <LoginSecurity email={user.userEmail} phone={user.phoneNumber} />
          );
        default:
          return <div>Please select an option from the sidebar.</div>;
      }
    } else {
      // alert("Please login to view this page.");
    }
  };

  useEffect(() => {
    if (!isLoggedIn || !user) {
      navigate("/login");
    }
  }, [isLoggedIn, user, navigate]);

  return (
    <Container className="py-5">
      <Row>
        <Col md={4}>
          <Sidebar2 onSelect={handleSelect} />
        </Col>
        <Col md={8}>{renderSelectedComponent()}</Col>
      </Row>
    </Container>
  );
}

